import React from 'react'

export default class Login extends React.Component {
  getLogin = () => {
    let getLoginText = document.querySelector("#textBox").value
    let url = `https://adventure-time-m4cap.herokuapp.com/api/v1/login?username=${getLoginText}`;
    fetch(url, { method: "POST" })
      .then(res => res.json())
      .then(person => this.props.get_user(person.Value))
      .then(this.props.display)
      .then(navigator.geolocation.getCurrentPosition(location => {console.log(location)}))
  }


  render() {
    return(
      <div style={styleLogin}>
        <p>Sign in Below!</p>
        <input id="textBox" type="text"/>
        <button onClick={this.getLogin} style={styleSubmit}>Login</button>
      </div>
    )
  }
}


const styleLogin = {
  display: "flex", 
  flexDirection: "column",
  textAlign: "center",
  padding: "2em",
  background: "rgba(206, 215, 219, 0.96)",
  borderRadius: "1em"
}

const styleSubmit = {
  marginTop: ".4em",
  marginLeft: "2em",
  marginRight: "2em"
}
